INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('7', '1', 'Disponibilidad de Servicios', 'SERV_AVAIL', 'Contar con disponibilidad para prestar sus servicios, de acuerdo con su especialidad, en los espacios acordados y requeridos por el supervisor según la necesidad del servicio.', '1', '7', '2025-11-05 14:14:21', '2025-11-05 14:14:21', NULL);
INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('8', '1', 'Tareas Asignadas por Supervisor', 'SUPER_TASKS', 'Las demás que le sean asignadas por el supervisor en relación con el objeto del contrato.', '1', '8', '2025-11-05 14:14:21', '2025-11-05 14:14:21', NULL);
INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('9', '2', 'Gestión de Publicaciones Digitales', 'GPD', 'Familia de servicio encargada de prestar apoyo en la creación, revisión y publicación de contenidos para los distintos canales digitales institucionales. Su propósito es garantizar que todas las publicaciones cumplan con los lineamientos estratégicos de comunicación digital, velando por la oportunidad, calidad, veracidad, pertinencia, claridad, objetividad y tiempos de entrega definidos por la coordinación del área. El alcance incluye el soporte continuo para mantener una comunicación digital alineada con los objetivos institucionales.', '1', '0', '2026-02-06 10:17:12', '2026-02-06 10:17:12', NULL);
INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('10', '2', 'Estrategia y Comunicación Digital', 'ECD', 'Familia de servicio responsable de apoyar la formulación, implementación y ejecución de la estrategia integral de comunicación digital del Ministerio. Su propósito es contribuir al posicionamiento, crecimiento y fidelización de las audiencias y comunidades en los entornos digitales institucionales. El alcance incluye el diseño y gestión de estrategias para las plataformas web y redes sociales oficiales, enfocadas en cumplir los objetivos de expectativa, alcance y engagement definidos por la institución.', '1', '0', '2026-02-06 10:18:28', '2026-02-06 10:18:28', NULL);
INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('11', '2', 'Gestión de la Parrilla y Análisis de Contenidos Digitales', 'GPACD', 'Familia de servicio encargada de diseñar, mantener y analizar la parrilla de contenidos para todos los medios digitales internos del Ministerio. Su propósito es establecer un registro diario sistemático de todas las publicaciones, que sirva como base para la generación de estadísticas, informes y métricas sobre temáticas cubiertas, áreas demandantes y formatos utilizados. El alcance garantiza la trazabilidad del contenido publicado y proporciona insumos de datos para la evaluación y mejora continua de la comunicación digital institucional.', '1', '0', '2026-02-06 10:19:09', '2026-02-06 10:19:09', NULL);
INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('12', '2', 'Gestión e Implementación de la Estrategia Digital Integral', 'EIDIG', 'Familia de servicio integral que abarca la totalidad del ciclo de vida digital de la entidad. Su propósito es acompañar la implementación de la Estrategia de Gobierno Digital (cumpliendo normativa MINTIC) y, simultáneamente, formular, ejecutar y analizar la Estrategia de Comunicación Digital (gestión de contenidos, parrillas, publicaciones y métricas). Actúa como un único punto de responsabilidad para alinear el cumplimiento normativo, los objetivos de comunicación y la experiencia digital de los ciudadanos.', '1', '0', '2026-02-06 10:20:39', '2026-02-06 15:21:45', '2026-02-06 15:21:45');
INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('13', '2', 'Cubrimiento y Registro de Eventos', 'CRE', 'Familia de servicio encargada de prestar apoyo operativo y logístico para el cubrimiento de eventos, actividades y agendas asignadas por el Ministerio. Su propósito es garantizar el registro audiovisual y documental (fotográfico, de video, notas) de los eventos tanto dentro como fuera de las instalaciones de la entidad, incluyendo desplazamientos a nivel nacional cuando se requiera. El alcance asegura la captura de material de calidad que posteriormente alimente los canales de comunicación digital y los archivos institucionales.', '1', '0', '2026-02-06 10:21:29', '2026-02-06 10:21:29', NULL);
INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('14', '2', 'Gestión Integral de la Estrategia Digital', 'GIED', 'Familia de servicio encargada de gestionar integralmente la estrategia, operación y cumplimiento normativo de la transformación digital del Ministerio. Su propósito es formular, ejecutar y evaluar la Estrategia de Comunicación Digital, lo que incluye el diseño de la parrilla de contenidos, el cubrimiento de eventos, la publicación en canales digitales y la generación de análisis de métricas. En paralelo, acompaña la implementación de la Estrategia de Gobierno Digital, garantizando el cumplimiento normativo con las directrices del Ministerio TIC. El alcance abarca la participación activa en todas las reuniones requeridas para el ciclo de la estrategia digital, velando por la alineación de los objetivos comunicativos, la calidad de los contenidos (oportunidad, veracidad, objetividad) y el marco regulatorio aplicable.', '1', '0', '2026-02-06 10:25:41', '2026-02-06 10:25:41', NULL);
INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('15', '2', 'Actividades de Soporte y Tareas Conexas del Contrato', 'ASTC', 'Familia de servicio designada para la ejecución de actividades, tareas o requerimientos específicos no previstos inicialmente, que sean asignados por la supervisión del contrato durante su vigencia. Su propósito es proveer flexibilidad operativa y capacidad de respuesta para atender necesidades puntuales de apoyo administrativo, logístico o técnico que se deriven de la gestión del contrato principal y que estén alineadas con su objeto. El alcance se define caso a caso según la asignación formal de la supervisión, siempre dentro del marco jurídico y presupuestal del contrato.', '1', '0', '2026-02-06 10:28:19', '2026-02-06 10:28:19', NULL);
INSERT INTO `service_families` (`id`, `contract_id`, `name`, `code`, `description`, `is_active`, `sort_order`, `created_at`, `updated_at`, `deleted_at`) VALUES ('17', '2', 'Gestión e Implementación de la Estrategia Digital Integral', 'GEID', 'Familia para la gestión e implementación de la estrategia digital integral.', '1', '12', '2026-02-06 15:01:22', '2026-02-06 15:01:22', NULL);
INSERT INTO `service_level_agreements` (`id`, `service_subservice_id`, `service_family_id`, `name`, `description`, `criticality_level`, `response_time_hours`, `resolution_time_hours`, `availability_percentage`, `acceptance_time_minutes`, `response_time_minutes`, `resolution_time_minutes`, `conditions`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('1', '1', '1', 'Correción de contenido publicado', NULL, 'ALTA', '2', '12', '99.90', '30', '120', '720', '**Contrato de Nivel de Servicio (SLA)**\r\n\r\nEste acuerdo establece los compromisos de servicio entre las partes. El proveedor garantiza una disponibilidad mensual del 99.5%, excluyendo los periodos de mantenimiento programado comunicados con antelación.\r\n\r\nPara incidencias, el tiempo de respuesta no excederá las 4 horas hábiles para casos de alta prioridad, con una resolución máxima en 24 horas. Estos plazos están sujetos a la recepción de toda la información necesaria por parte del cliente.\r\n\r\nSe emitirá un informe mensual de desempeño. Quedan exentos de cumplimiento los fallos causados por fuerza mayor o por falta de colaboración oportuna del cliente.', '1', '2025-11-05 14:57:38', '2025-11-05 15:48:01', NULL);
INSERT INTO `service_level_agreements` (`id`, `service_subservice_id`, `service_family_id`, `name`, `description`, `criticality_level`, `response_time_hours`, `resolution_time_hours`, `availability_percentage`, `acceptance_time_minutes`, `response_time_minutes`, `resolution_time_minutes`, `conditions`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('2', '3', '8', 'Correción de contenido de acuerdo con el requerimeinto asociado a la migración.', NULL, 'CRITICA', '2', '8', '99.90', '30', '150', '480', 'Creado automáticamente desde el formulario de solicitud de servicio', '1', '2025-11-05 18:58:24', '2025-11-05 19:16:10', NULL);
INSERT INTO `service_level_agreements` (`id`, `service_subservice_id`, `service_family_id`, `name`, `description`, `criticality_level`, `response_time_hours`, `resolution_time_hours`, `availability_percentage`, `acceptance_time_minutes`, `response_time_minutes`, `resolution_time_minutes`, `conditions`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('3', '4', '1', 'Ajustes asociados a el menu principal', 'Todos ajustes del menú principal', 'MEDIA', '1', '4', '99.90', '30', '60', '240', 'Creado automáticamente desde el formulario de solicitud de servicio', '1', '2025-11-05 19:04:43', '2025-11-05 19:04:43', NULL);
INSERT INTO `service_level_agreements` (`id`, `service_subservice_id`, `service_family_id`, `name`, `description`, `criticality_level`, `response_time_hours`, `resolution_time_hours`, `availability_percentage`, `acceptance_time_minutes`, `response_time_minutes`, `resolution_time_minutes`, `conditions`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('4', '5', '6', 'SLA para Publicación de Banners en el Home del Portal Web SDM', 'Este acuerdo define los niveles de servicio para la gestión de solicitudes de publicación, actualización y despublicación de banners promocionales o informativos en la página de inicio (home) del Portal Web SDM, garantizando los tiempos de atención y la disponibilidad del servicio.', 'MEDIA', '4', '8', '99.50', '60', '240', '480', '<ul><li>Los tiempos de Aceptación, Respuesta y Resolución se calculan en horas hábiles (ej: de Lunes a Viernes, 9:00 a 18:00).</li><li>Las solicitudes deben incluir todos los assets finales (imágenes, textos, enlaces) y la aprobación correspondiente del área solicitante.</li><li>Para banners de campañas críticas o de alto impacto (ej: lanzamientos oficiales, comunicados urgentes), se puede activar un procedimiento de publicación express con tiempos reducidos, previa validación y priorización.</li><li>Este SLA no cubre el tiempo de diseño o desarrollo creativo del banner, solo el proceso de publicación una vez recibidos los materiales finales.</li><li>El cálculo de disponibilidad excluye ventanas de mantenimiento programado anunciado con 72 horas de antelación.</li></ul>', '1', '2025-11-05 21:03:28', '2025-11-05 21:03:28', NULL);
INSERT INTO `service_level_agreements` (`id`, `service_subservice_id`, `service_family_id`, `name`, `description`, `criticality_level`, `response_time_hours`, `resolution_time_hours`, `availability_percentage`, `acceptance_time_minutes`, `response_time_minutes`, `resolution_time_minutes`, `conditions`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('5', '6', '6', 'SLA para Publicación de Documentos en el Portal Web SDM', 'Este acuerdo define los niveles de servicio para la gestión de solicitudes de publicación, actualización o eliminación de documentos (manuales, normativas, formularios, instructivos) en el Portal Web SDM. El servicio incluye la verificación del formato, la ubicación en la estructura del portal y la configuración de permisos de acceso según el requerimiento.', 'MEDIA', '6', '16', '99.50', '120', '360', '960', '<ul><li>Los tiempos de Aceptación, Respuesta y Resolución se calculan en horas hábiles (ej: de Lunes a Viernes, 9:00 a 18:00).</li><li>El tiempo de resolución puede variar significativamente dependiendo de la complejidad del requerimiento (ej.: publicar un PDF en una página existente vs. crear una nueva sección con múltiples documentos y permisos específicos).</li><li>La solicitud debe incluir el documento en versión final, la ubicación exacta deseada en el portal, la fecha de vigencia (si aplica) y el detalle de los perfiles de usuario que deben tener acceso.</li><li>No incluye la redacción, revisión legal o diseño gráfico del documento, solo su publicación técnica.</li><li>Para documentos de carácter Crítico (ej: normativas de cumplimiento obligatorio inmediato), se activará un canal prioritario con objetivos de resolución de 4 horas hábiles, sujeto a disponibilidad del equipo y aprobación por la gerencia.</li><li>El cálculo de disponibilidad excluye ventanas de mantenimiento programado anunciado con 72 horas de antelación.</li></ul>', '1', '2025-11-05 22:07:14', '2025-11-05 22:07:14', NULL);
INSERT INTO `service_level_agreements` (`id`, `service_subservice_id`, `service_family_id`, `name`, `description`, `criticality_level`, `response_time_hours`, `resolution_time_hours`, `availability_percentage`, `acceptance_time_minutes`, `response_time_minutes`, `resolution_time_minutes`, `conditions`, `is_active`, `created_at`, `updated_at`, `deleted_at`) VALUES ('6', '7', '6', 'Tiempo de Publicación de Contenidos para el Portal Web SDM', 'Este SLA define los acuerdos de nivel de servicio para la gestión, revisión y publicación de noticias, PMTs (Planes de Manejo de Tránsito) o artículos solicitados para el Portal Web SDM, garantizando la disponibilidad de información actualizada y de calidad para los usuarios finales.', 'MEDIA', '4', '24', '99.50', '60', '240', '1440', 'Los tiempos de resolución se cuentan a partir de la recepción de todo el contenido necesario y aprobado por el área solicitante.\r\n\r\n    Para publicaciones de alta prioridad (definidas por la Dirección de Comunicaciones), el tiempo de resolución se reduce a 8 horas laborales.\r\n\r\n    Este SLA no cubre la redacción o creación del contenido, solo su gestión editorial y publicación técnica en el portal.\r\n\r\n    Las solicitudes recibidas fuera del horario laboral se considerarán recibidas al inicio del siguiente día hábil.', '1', '2025-11-06 15:17:52', '2025-11-06 15:17:52', NULL);
